		String oaxml = "<?xml version='1.0' encoding='utf-8' ?><bulletin xmlns='http://www.gpcsoft.com'><header><pmOrgId>31</pmOrgId><pmUserId>474</pmUserId></header><body><projectInfo><projCode>GXTC-222</projCode><bulletinTitle>同步招标项目信息111招标公告</bulletinTitle><bulletinContent>"+bullContent+"</bulletinContent><signStartTime></signStartTime><signEndTime>2011-12-02 00:00:00</signEndTime><bidStartTime>2011-12-20 10:00:00</bidStartTime><bidEndTime>2012-03-18 00:00:00</bidEndTime><openBidTime>2011-12-20 10:00:00</openBidTime><meetRoomAddress>国信会议室</meetRoomAddress><bulletinUrl></bulletinUrl></projectInfo></body></bulletin>";
